
public class IfElseEX {

	public static void main(String args[]) {
		// TODO Auto-generated method stub
		
		int num1 = 50;
		int num2 = 50;
		
		if(num1 > num2)
		{
			System.out.println(num1 +" is greater");
		}
		else if(num2 > num1)
		{
			System.out.println(num2 +" is greater");
		}
		else {
			System.out.println("BOTH ARE EQUAL");
		}
	}

}
