
public class PostAndPre {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 5;
		int j = 0;
		int k = 3;
		int  ans = --j - --j + j++ - ++i - --i + k++;
		System.out.println(i);
		System.out.println(j);
		System.out.println(k);
		System.out.println(ans);
		
		
	}

}
